import React from 'react';
import {Button, Card} from 'react-bootstrap';

export default function DoraList(props) {
    let cardList = props.darayaki.map((singleData, index) => {
      return <DoraCard infoDisplay={singleData} key={index} />;
    })

    return (
      <div className="row">
        {cardList}
      </div>
    );
  }

function DoraCard(props) {
  let dora = props.infoDisplay;

  return (
    <div class="container">
      <div class="column col-md-6 col-xl-3">
        <Card>
          <Card.Img className="card-img" variant="top" src="img/cardImg.png" />
          <Card.Body>
            <Card.Title>{dora.title}</Card.Title>
            <Button variant="primary">Button Label</Button>

            <Card.Text>
              {dora.desc1}
              {dora.desc2}
            </Card.Text>
          </Card.Body>
        </Card>
      </div>
    </div>

  );
}
