      <Card className={`${cardIndex === activeItem} column col-8 col-md-4 col-xl-3 mx-2`} onMouseLeave={handleUnshown} onMouseEnter={handleHover} onMouseOver={handleIn}>
        <img className="card-img" src="img/cardImg.png" />
        <Card.Body>
          <h2 className="doraTitle">{dora.title}</h2>
          <Button className='button-label'> Button Label</Button>

