import React, { useState } from 'react';
import {Button, Card} from 'react-bootstrap';
export default function DoraList(props) {
  let cardList = props.darayaki.map((singleData) => {
    return (
      <DoraCard 
        infoDisplay={singleData} 
        key={singleData.id}
        num={singleData.id}
      />
    );
  })

    return (
      <div className="row justify-content-center">
        {cardList}
      </div>
    );
  }

function DoraCard(props) {
  let dora = props.infoDisplay;
  let [activeItem, setActiveItem] = useState(true);
  const [toggle, setToggle] = React.useState({});

  function toggleFunction(id) {
    setToggle({
      ...toggle,
      [id]: !toggle[id],
    });
  }

  // const handleHover = (e) => {
  //   let x = e.clientX / window.innerWidth;
  //   let y = e.clientY / window.innerHeight;
  //   document.documentElement.style.setProperty("--mouse-x", x);
  //   document.documentElement.style.setProperty("--mouse-y", y);
  //   console.log(activeItem)
  // }

  // const handleUnshown = () => {
  //   let x = -5;
  //   let y = -5;
  //   document.documentElement.style.setProperty("--mouse-x", x);
  //   document.documentElement.style.setProperty("--mouse-y", y);
  //   console.log(activeItem)

  // }

  return (
      <Card className="column col-8 col-md-4 col-xl-3 mx-2" onClick={() => toggleFunction(props.num)}>
        <img className="card-img" src="img/cardImg.png" />
        <Card.Body>
          <h2 className="doraTitle">{dora.title}</h2>
          <Button className='button-label' style={{ display: toggle[props.num] ? 'background-color' : 'red' }}> Button Label</Button>

          <div className='desc1'>
            <div className='desc'>
                {dora.desc1}
            </div>

            <div className='desc description'>
                Description
            </div>
          </div>

          <div className='desc2'>
            <div className='desc'>
                {dora.desc2}
            </div>

            <div className='desc description'>
                Description
            </div>
          </div>

        </Card.Body>
      </Card> 
  );
}
