import React from 'react';

export default function DoraList(props) {
    let cardList = props.darayaki.map((singleData, index) => {
      return <DoraCard infoDisplay={singleData} key={index} />;
    })

    return (
      <div>
        <div className="card-deck">
          {cardList}
        </div>
      </div>
    );
  }

function DoraCard(props) {
  let dora = props.infoDisplay;

  return (
    <div className="card column col-md-6 col-xl-3">
      <div className="card mb-4 shadow-sm">

        <img className="card-img-top" src={dora.img} alt={dora.name} />
        <div className="card-body">
          <h3 className="card-title">{dora.title} </h3>
          <p className="card-text">{dora.desc1} {dora.desc2}</p>
        </div>
      </div>
    </div>

  );
}
