import React, { useState } from 'react';
import {Button, Card} from 'react-bootstrap';
export default function DoraList(props) {
  let cardList = props.darayaki.map((singleData) => {
    const [toggle, setToggle] = React.useState({});

    function toggleFunction(id) {
      setToggle({
        ...toggle,
        [id]: !toggle[id],
      });
    }
    return (
      <DoraCard 
        infoDisplay={singleData} 
        key={singleData.id}
        num={singleData.id}
        onMouseOver={() => toggleFunction(props.num)} onMouseLeave={() => toggleFunction()}
      />
    );
  })

    return (
      <div className="row justify-content-center">
        {cardList}
      </div>
    );
  }

function DoraCard(props) {
  let dora = props.infoDisplay;


  // const handleHover = (e) => {
