  //   document.documentElement.style.setProperty("--mouse-y", y);
  //   console.log(activeItem)

  // }

  return (
      <Card className="column col-8 col-md-4 col-xl-3 mx-2" >
        <img className="card-img" src="img/cardImg.png" />
        <Card.Body>
          <h2 className="doraTitle">{dora.title}</h2>
          <Button className='button-label' style={{ display: toggle[props.num] ? 'block' : 'none' }} > Button Label</Button>

          <div className='desc1'>
            <div className='desc'>
                {dora.desc1}
            </div>

            <div className='desc description'>
                Description
            </div>
          </div>

          <div className='desc2'>
            <div className='desc'>
                {dora.desc2}
            </div>

            <div className='desc description'>
                Description
            </div>
          </div>

        </Card.Body>
      </Card> 
  );
}
