import React, { useState } from 'react';
import {Button, Card} from 'react-bootstrap';
export default function DoraList(props) {

  // <DoraCard infoDisplay={singleData} key={index}/>;
  const [isShown, setIsShown] = useState(false);

  function onHover (e) {
    let x = e.clientX / window.innerWidth;
    let y = e.clientY / window.innerHeight;

    document.documentElement.style.setProperty("--mouse-x", x);
    document.documentElement.style.setProperty("--mouse-y", y);
  }

  const handleShown = () => {
    if(isShown) {
      setIsShown(!isShown);
    }
  }

  const handleUnshown = () => {
    setIsShown(!isShown);
    let x = -50;
    let y = -50;
    document.documentElement.style.setProperty("--mouse-x", x);
    document.documentElement.style.setProperty("--mouse-y", y);
  }

  let cardList = props.darayaki.map((singleData, index) => {
    return isShown ? <DoraCard infoDisplay={singleData} key={index} onMouseMove={onHover} onMouseEnter={handleShown} onMouseLeave={handleUnshown}/> : <DoraCard infoDisplay={singleData} key={index}/>
  })

  return (
    <div className="row justify-content-center">
      {cardList}
    </div>
  );
}


function DoraCard(props) {
  let dora = props.infoDisplay;

  return (
      <Card className="column col-8 col-md-4 col-xl-3 mx-2">
        <img className="card-img" variant="top" src="img/cardImg.png" />
        <Card.Body>
          <h2 className="doraTitle">{dora.title}</h2>
          <Button className='button-label'> Button Label</Button>

          <div className='desc1'>
            <div className='desc'>
                {dora.desc1}
            </div>

            <div className='desc description'>
                Description
            </div>
          </div>

          <div className='desc2'>
            <div className='desc'>
                {dora.desc2}
            </div>

            <div className='desc description'>
                Description
            </div>
          </div>

        </Card.Body>
      </Card>

  );
}
