import React, { useState } from 'react';
import {Button, Card} from 'react-bootstrap';
export default function DoraList(props) {
    let cardList = props.darayaki.map((singleData, index) => {
      return <DoraCard infoDisplay={singleData} key={index} />;
    })

    return (
      <div className="row justify-content-center">
        {cardList}
      </div>
    );
  }

function onHover (e) {
  let x = e.clientX / window.innerWidth;
  let y = e.clientY / window.innerHeight;

  document.documentElement.style.setProperty("--mouse-x", x);
  document.documentElement.style.setProperty("--mouse-y", y);
}

function DoraCard(props) {
  const [isShown, setIsShown] = useState(false);

  const handleShown = () => {
    if(isShown) {

      setIsShown(!isShown);
    }
  }

  const handleUnshown = () => {
    setIsShown(!isShown);
    let x = -50;
    let y = -50;

    document.documentElement.style.setProperty("--mouse-x", x);
    document.documentElement.style.setProperty("--mouse-y", y);
  }

  let dora = props.infoDisplay;

  if(!isShown) {

    return (
        <Card className="column col-8 col-md-4 col-xl-3 mx-2" onMouseMove={onHover} onMouseEnter={handleShown} onMouseLeave={handleUnshown}>
          <img className="card-img" variant="top" src="img/cardImg.png" />
          <Card.Body>
            <h2 className="doraTitle">{dora.title}</h2>
            <Button className='button-label'> Button Label</Button>

            <div className='desc1'>
              <div className='desc'>
                  {dora.desc1}
              </div>

              <div className='desc description'>
                  Description
              </div>
            </div>

            <div className='desc2'>
              <div className='desc'>
                  {dora.desc2}
              </div>

              <div className='desc description'>
                  Description
              </div>
            </div>

          </Card.Body>
        </Card> 
    );
  } else {    
    return (
    <Card className="column col-8 col-md-4 col-xl-3 mx-2" onMouseEnter={handleShown} onMouseLeave={handleUnshown}>
      <img className="card-img" variant="top" src="img/cardImg.png" />
      <Card.Body>
        <h2 className="doraTitle">{dora.title}</h2>
        <Button className='button-label'> Button Label</Button>

        <div className='desc1'>
          <div className='desc'>
              {dora.desc1}
          </div>

          <div className='desc description'>
              Description
          </div>
        </div>

        <div className='desc2'>
          <div className='desc'>
              {dora.desc2}
          </div>

          <div className='desc description'>
              Description
          </div>
        </div>

      </Card.Body>
    </Card> 
);

  }
}
